/** @format */

import styled from 'styled-components';

interface OptionsStyledProps {
	selected: boolean;
}

export const OptionsStyled = styled.div<OptionsStyledProps>`
	width: 100%;
	padding: 5px 50px;
	background-color: {props => props.selected ? yellow};
	border-radius: 10px;
  color: var(--colorContainer);
  cursor: pointer;
  margin-bottom: 3px;


`;
