/** @format */

import styled from 'styled-components';

export const HomeStyled = styled.div`
	height: 100vh;
	display: flex;
	flex-direction: column;
	align-items: center;

	& > div {
		max-width: 600px;
		display: flex;
		flex-direction: column;
		align-items: center;

		h3 {
			color: var(--blue);
			margin-bottom: 45px;
			text-align: center;
		}

		input {
			border-radius: 20px;
			border: none;
			height: 35px;
			width: 80%;
			padding: 10px;

		}
	}
`;
