/** @format */

import styled from 'styled-components';

export const QuestionStyled = styled.div`
	> div {
		width: 80%;
		margin: auto;
		padding: 50px 700px;
		h2 {
			color: var(--yellow);
			margin-bottom: 30px;
			text-align: start;
		}
    p {
      color: var(--white);
      font-size: 1.2rem;
    }
	}
`;
