/** @format */

import { createGlobalStyle } from 'styled-components';

export const StyledGlobal = createGlobalStyle`
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

html,
body {
  background-color: #191919;
  height: 100vh;
  font-size: 16px; 
}
`;
